mapper.select:

	Select Rownum As rn, b.*
	FROM	(
	        Select b.*, u.nick,
	                Row_Number() Over (Order By brd_num Desc) As row_num                
	        From    board b
	                INNER JOIN  user1 u On  b.user_num = u.user_num
	        Where   brd_md= 100
	                and     brd_lg		=	700
	                and     brd_step	=	0
	                and     chg_id		=	#{chg_id}
	        ) b
	Where b.row_num Between #{start} 
					And 	#{end}

mapper.update:
Update	board
Set		title = #{title}, conts = #{conts}
Where	brd_num = #{brd_num}
