mapper.update:
UPDATE 	user1
SET	   	delete_yn = 'Y'
WHERE  	user_id   = #{user_id}
AND		user_pswd = #{user_pswd}

UPDATE 	user1
SET	   	status_md = 101
WHERE  	user_num   = #{user_num}

mapper.select:
SELECT 	COUNT(*)
FROM	user1
WHERE	user_id	=	#{user_id}

SELECT 	Count(*)
FROM	User1
WHERE	nick	=	#{nick}
